mapper.select:
select a_id, a_date, a_title, a_contents, a_etc, a_t_id, a_o_id
	   ,af_id, af_rfilename, af_sfilename 
from activefile af,
	 (select a_id, to_char(a_date,'yyyy-mm-dd') a_date, a_title 
		, a_contents , a_etc , a_t_id, a_o_id
		from active
		where a_id = #{a_id}
	 )a
where a.a_id = af.af_a_id(+)

select distinct af_id, af_rfilename, af_sfilename, a_title af_a_title, af_a_id  
from activefile af,
	 (select a_id, a_title 
		from active
		
			
				
					
						a_title like '%'|| #{word} || '%'
					
					
						a_id = #{word}
					
				
			
		
	 )a
where a.a_id = af.af_a_id(+)
order by af_id desc

mapper.insert:
insert into active ( a_id, a_date, a_title 
					, a_contents , a_etc , a_t_id, a_o_id )
values( #{a_id:INTEGER}
		,sysdate
		,#{a_title:VARCHAR}
		,#{a_contents:VARCHAR}
		,#{a_etc:VARCHAR}
		,#{a_t_id:INTEGER}
		,#{a_o_id:INTEGER}
	)

insert into activefile(af_id, af_rfilename, af_sfilename, af_a_id)
values( af_seq.nextval
		,#{af_rfilename:VARCHAR}
		,#{af_sfilename:VARCHAR}
		,#{af_a_id:INTEGER}
		)

mapper.update:
update active set a_date=sysdate
		,a_title=#{a_title:VARCHAR}
		,a_contents=#{a_contents:VARCHAR}
		,a_etc=#{a_etc:VARCHAR}
where a_id=#{a_id:INTEGER}
