mapper.insert:
insert into board ( b_id, b_date, b_title 
					, b_contents , b_etc , b_t_id )
values( #{b_id:INTEGER}
		,sysdate
		,#{b_title:VARCHAR}
		,#{b_contents:VARCHAR}
		,#{b_etc:VARCHAR}
		,#{b_t_id:INTEGER}
	)

mapper.update:
update board set b_date=sysdate
		,b_title=#{b_title:VARCHAR}
		,b_contents=#{b_contents:VARCHAR}
		,b_etc=#{b_etc:VARCHAR}
		,b_t_id=#{b_t_id:INTEGER}
where b_id=#{b_id:INTEGER}
