mapper.select:
select count(*) from kid
k_name like '%'|| #{word} || '%'
k_p_id = #{word}

mapper.insert:
insert into kid ( k_id, k_name, k_birth, k_sex, k_img
					, k_contents, k_p_id, k_date )
values( #{k_id:INTEGER}
		,#{k_name:VARCHAR}
		,#{k_birth:VARCHAR}
		,#{k_sex:VARCHAR}
		,#{k_img:VARCHAR}
		,#{k_contents:VARCHAR}
		,#{k_p_id:INTEGER}
		,sysdate
	)

mapper.update:
update kid set k_name=#{k_name:VARCHAR}
		,k_birth=#{k_birth:VARCHAR}
		,k_sex=#{k_sex:VARCHAR}
		,k_img=#{k_img:VARCHAR}
		,k_contents=#{k_contents:VARCHAR}
		,k_p_id=#{k_p_id:INTEGER}
		,k_date=sysdate
where k_id=#{k_id:INTEGER}
